const dsFile = {
    "https://maxcdn.bootstrapcdn.com/bootstrap/4.4.1/css/bootstrap.min.css": {
      "rel": "stylesheet"
    },
    "https://cdn.materialdesignicons.com/5.0.45/css/materialdesignicons.min.css": {
      "rel": "stylesheet"
    },
    "https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.16.0/umd/popper.min.js": {},
    "https://maxcdn.bootstrapcdn.com/bootstrap/4.4.1/js/bootstrap.min.js": {},
    "https://use.fontawesome.com/releases/v5.12.1/js/all.js": {
        "data-auto-replace-svg": "nest"
    },
    "../css/index.css": {
      "rel": "stylesheet"
    }
};

if (dsFile) {
    for (current_file in dsFile) {
        const extension = /.\w+$/.exec(current_file)[0];
        switch(extension) {
            case ".js":
                const script = document.createElement("script");
                script.src = current_file;
                document.head.appendChild(script);
            break;

            case ".css":
                const link = document.createElement("link");
                link.href = current_file;
                link.type = "text/css";
                link.rel = "stylesheet";
                document.head.appendChild(link);
            break;
        }
    }
}